import React, { useState } from 'react';
import axios from 'axios';

function TextAnalyzer() {
  const [input, setInput] = useState('');
  const [result, setResult] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const analyzeText = async () => {
    if (!input.trim()) return;

    setIsLoading(true);
    try {
      // Debug environment variables
      console.log('Environment variables:', {
        apiKey: process.env.REACT_APP_AZURE_OPENAI_API_KEY,
        endpoint: process.env.REACT_APP_AZURE_OPENAI_ENDPOINT
      });

      // Use default endpoint if environment variable is not loaded
      const baseEndpoint = process.env.REACT_APP_AZURE_OPENAI_ENDPOINT || 'https://thread-knowledge.openai.azure.com';
      const endpoint = baseEndpoint.replace(/\/$/, '');
      const url = `${endpoint}/openai/deployments/gpt-4o-mini-s/chat/completions?api-version=2024-02-01`;

      console.log('Final URL:', url);

      const response = await axios.post(
        url, 
        {
          messages: [
            { role: "user", content: input }
          ],
          max_tokens: 100,
          temperature: 0.5
        },
        {
          headers: {
            'Content-Type': 'application/json',
            'api-key': process.env.REACT_APP_AZURE_OPENAI_API_KEY || ''
          }
        }
      );

      setResult(response.data.choices[0].message.content);
    } catch (error) {
      console.error('Full error:', error);
      setResult(`Error: Environment variables not loaded properly. Please check your .env file and restart the development server.`);
    }
    setIsLoading(false);
  };

  // Remove handleAnalyze function as it's not being used

  return (
    <div style={{ maxWidth: '800px', margin: '40px auto', padding: '20px' }}>
      <div style={{ marginBottom: '20px' }}>
        <label style={{ display: 'block', marginBottom: '10px', fontWeight: 'bold' }}>Input Text:</label>
        <textarea
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Enter your text here..."
          style={{ width: '100%', height: '200px', padding: '10px', marginBottom: '20px' }}
        />
      </div>

      <button 
        onClick={analyzeText}
        disabled={isLoading}
        style={{ 
          width: '100%', 
          padding: '10px', 
          backgroundColor: '#0066cc', 
          color: 'white', 
          border: 'none',
          marginBottom: '20px'
        }}
      >
        {isLoading ? 'Analyzing...' : 'Analyze Text'}
      </button>

      <div style={{ marginBottom: '20px' }}>
        <label style={{ display: 'block', marginBottom: '10px', fontWeight: 'bold' }}>Generated Analysis:</label>
        <textarea
          value={result}
          readOnly
          placeholder="Analysis will appear here..."
          style={{ 
            width: '100%', 
            height: '200px', 
            padding: '10px',
            backgroundColor: '#f5f5f5'
          }}
        />
      </div>
    </div>
  );
}

export default TextAnalyzer;
