import React, { useState } from 'react';
import axios from 'axios';

function TextAnalyzer() {
  const [input, setInput] = useState('');
  const [result, setResult] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const analyzeText = async () => {
    if (!input.trim()) return;

    setIsLoading(true);
    try {
      const response = await axios.post('/api/analyze', { text: input });
      setResult(response.data.result);
    } catch (error) {
      console.error('Error:', error);
    }
    setIsLoading(false);
  };

  return (
    <div style={{ maxWidth: '800px', margin: '40px auto', padding: '20px' }}>
      <div style={{ marginBottom: '20px' }}>
        <label style={{ display: 'block', marginBottom: '10px', fontWeight: 'bold' }}>Input Text:</label>
        <textarea
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Enter your text here..."
          style={{ width: '100%', height: '200px', padding: '10px', marginBottom: '20px' }}
        />
      </div>

      <button 
        onClick={analyzeText}
        disabled={isLoading}
        style={{ 
          width: '100%', 
          padding: '10px', 
          backgroundColor: '#0066cc', 
          color: 'white', 
          border: 'none',
          marginBottom: '20px'
        }}
      >
        {isLoading ? 'Analyzing...' : 'Analyze Text'}
      </button>

      <div style={{ marginBottom: '20px' }}>
        <label style={{ display: 'block', marginBottom: '10px', fontWeight: 'bold' }}>Generated Analysis:</label>
        <textarea
          value={result}
          readOnly
          placeholder="Analysis will appear here..."
          style={{ 
            width: '100%', 
            height: '200px', 
            padding: '10px',
            backgroundColor: '#f5f5f5'
          }}
        />
      </div>
    </div>
  );
}

export default TextAnalyzer;
