import React, { useState, useEffect } from 'react';
import axios from 'axios';
import * as XLSX from 'xlsx';

function TextAnalyzer() {
  const [input, setInput] = useState('');
  const [result, setResult] = useState('');
  const [searchInput, setSearchInput] = useState('');
  const [matchedProducts, setMatchedProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Constants for Azure OpenAI
  const AZURE_ENDPOINT = "https://thread-knowledge.openai.azure.com";
  const DEPLOYMENT_NAME = "gpt-4o-mini-s";
  const API_VERSION = "2024-02-01";
  const API_KEY = "ded9fbd4adb645d7a2fadf016761922f";

  const analyzeText = async () => {
    if (!input.trim()) {
      setError('Please enter some text to analyze');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const url = `${AZURE_ENDPOINT}/openai/deployments/${DEPLOYMENT_NAME}/chat/completions?api-version=${API_VERSION}`;

      const response = await axios({
        method: 'post',
        url: url,
        headers: {
          'Content-Type': 'application/json',
          'api-key': API_KEY
        },
        data: {
          messages: [
            {
              role: "system",
              content: `Extract the following information from the input text and return it in JSON format:
              {
                "customer_name": "",
                "company_name": "",
                "company_address": "",
                "products": [
                  {
                    "part_name": "",
                    "part_id": "",
                    "quantity": ""
                  }
                ],
                "competitor_name": "",
                "discount_mentioned": false
              }

              Guidelines:
              - Create a new product object in the products array for each product/part mentioned
              - Fill in all fields that can be found in the text
              - Leave fields empty ("") if information is not present
              - For quantity, include units if specified
              - Set discount_mentioned to true if any discount is mentioned in the text
              - Ensure exact matches for part IDs
              - Return only the JSON object, no additional text`
            },
            {
              role: "user",
              content: input
            }
          ],
          max_tokens: 800,
          temperature: 0.3, // Lower temperature for more precise extraction
          frequency_penalty: 0,
          presence_penalty: 0,
          top_p: 0.95
        }
      });

      if (response.data && response.data.choices && response.data.choices[0]) {
        setResult(response.data.choices[0].message.content);
      } else {
        setError('Received invalid response format from API');
      }
    } catch (err) {
      console.error('API Error:', err);
      setError(
        err.response?.data?.error?.message || 
        err.message || 
        'Failed to analyze text'
      );
    } finally {
      setIsLoading(false);
    }
  };

  const searchProducts = async () => {
    try {
      const response = await fetch('/PIM_part_name_and_id.xlsx');
      const arrayBuffer = await response.arrayBuffer();
      const workbook = XLSX.read(arrayBuffer);
      const worksheet = workbook.Sheets[workbook.SheetNames[0]];
      const data = XLSX.utils.sheet_to_json(worksheet);

      const parsedResult = JSON.parse(result);
      const searchTerms = parsedResult.products.map(p => ({
        part_name: p.part_name.toLowerCase(),
        part_id: p.part_id,
        quantity: p.quantity
      }));

      const matches = data.filter(row => 
        searchTerms.some(term => 
          row.part_name?.toLowerCase().includes(term.part_name) ||
          row.part_id === term.part_id
        )
      ).map(match => ({
        ...match,
        quantity: searchTerms.find(t => 
          t.part_name === match.part_name?.toLowerCase() ||
          t.part_id === match.part_id
        )?.quantity || ''
      }));

      setMatchedProducts(matches);
    } catch (err) {
      setError('Error searching products: ' + err.message);
    }
  };

  return (
    <div style={{ maxWidth: '800px', margin: '40px auto', padding: '20px' }}>
      <div style={{ marginBottom: '20px' }}>
        <label style={{ display: 'block', marginBottom: '10px', fontWeight: 'bold' }}>
          Input Text:
        </label>
        <textarea
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Enter your text here..."
          style={{ 
            width: '100%', 
            height: '200px', 
            padding: '10px', 
            marginBottom: '10px',
            borderRadius: '4px',
            border: '1px solid #ccc'
          }}
        />
      </div>

      {error && (
        <div style={{ 
          color: 'red', 
          marginBottom: '20px', 
          padding: '10px', 
          backgroundColor: '#fff3f3',
          borderRadius: '4px'
        }}>
          {error}
        </div>
      )}

      <button 
        onClick={analyzeText}
        disabled={isLoading}
        style={{ 
          width: '100%', 
          padding: '12px', 
          backgroundColor: isLoading ? '#cccccc' : '#0066cc', 
          color: 'white', 
          border: 'none',
          borderRadius: '4px',
          cursor: isLoading ? 'not-allowed' : 'pointer',
          marginBottom: '20px'
        }}
      >
        {isLoading ? 'Analyzing...' : 'Analyze Text'}
      </button>

      <div style={{ marginBottom: '20px' }}>
        <label style={{ display: 'block', marginBottom: '10px', fontWeight: 'bold' }}>
          Analysis Result:
        </label>
        <textarea
          value={result}
          readOnly
          placeholder="Analysis will appear here..."
          style={{ 
            width: '100%', 
            height: '200px', 
            padding: '10px',
            backgroundColor: '#f5f5f5',
            borderRadius: '4px',
            border: '1px solid #ccc'
          }}
        />
      </div>

      <button 
        onClick={searchProducts}
        disabled={!result || isLoading}
        style={{ 
          width: '100%', 
          padding: '12px', 
          backgroundColor: !result || isLoading ? '#cccccc' : '#0066cc', 
          color: 'white', 
          border: 'none',
          borderRadius: '4px',
          cursor: !result || isLoading ? 'not-allowed' : 'pointer',
          marginBottom: '20px'
        }}
      >
        Search Products
      </button>

      {matchedProducts.length > 0 && (
        <div style={{ marginTop: '20px' }}>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={tableHeaderStyle}>Part Name</th>
                <th style={tableHeaderStyle}>Part ID</th>
                <th style={tableHeaderStyle}>Quantity</th>
              </tr>
            </thead>
            <tbody>
              {matchedProducts.map((product, index) => (
                <tr key={index}>
                  <td style={tableCellStyle}>{product.part_name}</td>
                  <td style={tableCellStyle}>{product.part_id}</td>
                  <td style={tableCellStyle}>{product.quantity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

const tableHeaderStyle = {
  backgroundColor: '#f5f5f5',
  padding: '10px',
  textAlign: 'left',
  border: '1px solid #ddd'
};

const tableCellStyle = {
  padding: '10px',
  border: '1px solid #ddd'
};

export default TextAnalyzer;
